import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
//import { css } from '@emotion/react';
import { SyncLoader } from 'react-spinners';

import './App.css';

Modal.setAppElement('#root');

const WordLadder = () => {
  const [startWord, setStartWord] = useState('');
  const [endWord, setEndWord] = useState('');
  const [wordLadder, setWordLadder] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const [isCalculating, setIsCalculating] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(true);
  const [isSuccess, setIsSuccess] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    setIsButtonDisabled(startWord === '' || endWord === '' || startWord.length !== endWord.length);
    setShowResult(false);
  }, [startWord, endWord]);

  const handleStartWordChange = (e) => {
    setStartWord(e.target.value.trim().toLowerCase().replace(/[^a-z]/g, ''));
    setShowResult(false);
  };

  const handleEndWordChange = (e) => {
    setEndWord(e.target.value.trim().toLowerCase().replace(/[^a-z]/g, ''));
    setShowResult(false);
  };

  const findWordLadder = () => {
    setIsCalculating(true);

    // Simulate API call to find the word ladder
    //setTimeout(() => {
      const ladder = ['hit', 'hot', 'dot', 'dog', 'cog'];
      setWordLadder(ladder);
      setIsSuccess(true);
      setIsCalculating(false);
      setShowResult(true);

      // Clear input boxes after success
      //setStartWord('');
      //setEndWord('');
   // }, 1000);
  };

  const handleFindWordLadder = () => {
    if (!isButtonDisabled && !isCalculating) {
      if (startWord === endWord) {
        setErrorMessage('Start and end words cannot be the same.');
        setIsSuccess(false);
      } else {
        setErrorMessage('');
        findWordLadder();
      }
    }
  };

  const handleCloseModal = () => {
    setErrorMessage('');
  };

  return (
    <div className="container d-flex align-items-center justify-content-center vh-100">
      <div className="word-ladder-container">
        <h1 className="title">Word Ladder</h1>
        <div className="input-group">
          <input
            type="text"
            className={`form-control ${startWord !== '' && 'active'}`}
            placeholder="Enter start word"
            value={startWord}
            onChange={handleStartWordChange}
          />
          <input
            type="text"
            className={`form-control ${endWord !== '' && 'active'}`}
            placeholder="Enter end word"
            value={endWord}
            onChange={handleEndWordChange}
          />
          <button
            onClick={handleFindWordLadder}
            className={`btn btn-primary animate__animated ${isCalculating ? 'animate__heartBeat' : ''}`}
            disabled={isButtonDisabled || isCalculating}
          >
            {isCalculating ? (
              <SyncLoader
                size={8}
                color={'#fff'}
              />
            ) : (
              'Find Word Ladder'
            )}
          </button>
        </div>

        {showResult && isSuccess && !isCalculating && (
          <div className="word-ladder-result">
            <h3>Word Ladder Result:</h3>
            <ul className="word-list">
              {wordLadder.map((word, index) => (
                <li key={index} className="word-list-item animate__animated animate__fadeInUp">
                  {word}
                </li>
              ))}
            </ul>
          </div>
        )}

        <Modal
          isOpen={errorMessage !== ''}
          onRequestClose={handleCloseModal}
          className="custom-modal"
          overlayClassName="custom-modal-overlay"
        >
          <div className="modal-content">
            <button className="close-btn" onClick={handleCloseModal}>
              &times;
            </button>
            <p>{errorMessage}</p>
          </div>
        </Modal>
      </div>
    </div>
  );
};

export default WordLadder;
