import React, { useState } from 'react';
//import { findLadder } from 'word-ladder-js';
import './App.css'; // Import the custom WordLadder.css file
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const WordLadder = () => {
  const [startWord, setStartWord] = useState('');
  const [endWord, setEndWord] = useState('');
  const [wordLadder, setWordLadder] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const [validationError, setValidationError] = useState(false);

  const handleStartWordChange = (e) => {
    setStartWord(e.target.value);
    setValidationError(false); // Clear validation error when input changes
  };

  const handleEndWordChange = (e) => {
    setEndWord(e.target.value);
    setValidationError(false); // Clear validation error when input changes
  };

  const handleFindWordLadder = () => {
    // Function to check if two words differ by only one character
    if (startWord.trim() === '' || endWord.trim() === '') {
      setValidationError(true);
      return;
    }
    const isAdjacent = (word1, word2) => {
      let diffCount = 0;
      for (let i = 0; i < word1.length; i++) {
        if (word1[i] !== word2[i]) {
          diffCount++;
          if (diffCount > 1) return false;
        }
      }
      return diffCount === 1;
    };

    // Breadth-First Search to find the word ladder
    const bfs = (start, end, wordList) => {
      const visited = new Set();
      const queue = [];
      queue.push([start]);

      while (queue.length > 0) {
        const currentPath = queue.shift();
        const lastWord = currentPath[currentPath.length - 1];

        if (lastWord === end) {
          return currentPath;
        }

        for (const word of wordList) {
          if (!visited.has(word) && isAdjacent(lastWord, word)) {
            visited.add(word);
            queue.push([...currentPath, word]);
          }
        }
      }

      return []; // Empty array means no word ladder found
    };

    // Sample word list (replace this with your actual word list)
    const wordList = ['hot', 'dot', 'dog', 'lot', 'log', 'cog'];

    const ladder = bfs(startWord, endWord, wordList);
    setWordLadder(ladder);
    setShowResult('visible');
  };

 // const isButtonDisabled = startWord.trim() === '' || endWord.trim() === '';

  return (
    <div className="word-ladder-container">
      <h1 className="text-center">Word Ladder</h1>
      <div className="row justify-content-center">
        <div className="col-md-4">
          <div className="input-container">
            <input
              type="text"
              value={startWord}
              onChange={handleStartWordChange}
              className={`form-control mb-2 ${validationError && startWord.trim() === '' ? 'border border-danger' : ''}`}
              placeholder="Enter start word"
            />
            <input
              type="text"
              value={endWord}
              onChange={handleEndWordChange}
              className={`form-control mb-2 ${validationError && endWord.trim() === '' ? 'border border-danger' : ''}`}
              placeholder="Enter end word"
            />
            <button
              onClick={handleFindWordLadder}
              className="btn btn-primary animate__animated animate__heartBeat"
              //disabled={isButtonDisabled}
            >
              Find Word Ladder
            </button>
          </div>
        </div>
        <div className={`col-md-4 ${showResult ? 'visible' : 'invisible'}`}>
          {wordLadder.length > 0 && (
            <div className="result-container mt-4">
              <h3 className="text-center">Word Ladder Result:</h3>
              <div className="scrollable-list">
                <ul className="list-unstyled text-center">
                  {wordLadder.map((word, index) => (
                    <li key={index} className="animate__animated animate__fadeInUp">
                      {word}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WordLadder;
