import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
// import { css } from '@emotion/react';
import { SyncLoader } from 'react-spinners';

Modal.setAppElement('#root');

const WordLadder = () => {
  const [startWord, setStartWord] = useState('');
  const [endWord, setEndWord] = useState('');
  const [wordLadder, setWordLadder] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(true);
  const [isCalculating, setIsCalculating] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [validationError, setValidationError] = useState(false);

  useEffect(() => {
    // Enable the solve button if the conditions are met
    setIsButtonDisabled(
      startWord === '' ||
      endWord === '' ||
      startWord.length !== endWord.length ||
      startWord === endWord ||
      showResult
    );
  }, [startWord, endWord, showResult]);

  const handleStartWordChange = (e) => {
    const input = e.target.value.replace(/[^a-zA-Z]/g, ''); // Allow only English alphabet characters
    setStartWord(input);
    setWordLadder([]);
    setShowResult(false);
    setValidationError(false);
  };

  const handleEndWordChange = (e) => {
    const input = e.target.value.replace(/[^a-zA-Z]/g, ''); // Allow only English alphabet characters
    setEndWord(input);
    setWordLadder([]);
    setShowResult(false);
    setValidationError(false);
  };

  const findWordLadder = (startWord, endWord, dictionary) => {
    // Convert the dictionary to a set for faster word lookups
    const wordSet = new Set(dictionary);

    if (!wordSet.has(endWord)) {
      return []; // If the end word is not in the dictionary, there's no ladder
    }

    // Queue to perform BFS
    const queue = [];
    queue.push([startWord, [startWord]]);

    while (queue.length > 0) {
      const [currentWord, currentLadder] = queue.shift();

      // Generate all possible next words
      for (let i = 0; i < currentWord.length; i++) {
        for (let j = 97; j <= 122; j++) {
          const nextWord =
            currentWord.slice(0, i) + String.fromCharCode(j) + currentWord.slice(i + 1);

          if (nextWord === currentWord) continue; // Skip if the same word

          if (wordSet.has(nextWord)) {
            // If the next word is in the dictionary, add it to the ladder
            const newLadder = [...currentLadder, nextWord];

            if (nextWord === endWord) {
              return newLadder; // If we found the end word, return the ladder
            }

            queue.push([nextWord, newLadder]);
            wordSet.delete(nextWord); // Mark the word as visited to avoid loops
          }
        }
      }
    }

    return []; // If no ladder is found, return an empty array
  };  

  const handleFindWordLadder = () => {
    if (isButtonDisabled) return;

    setIsCalculating(true);

    setTimeout(() => {
      // ... (existing word ladder algorithm logic remains the same)
      const wordList = [
        "hot", "dot", "dog", "lot", "log", "cog",
        "pot", "pig", "sog", "sot", "cot", "bog",
        "bot", "big", "bit"
      ];
      const ladder = findWordLadder(startWord, endWord, wordList);

      if (ladder.length === 0) {
        setErrorMessage('Word ladder not found!');
      } else {
        setWordLadder(ladder);
        setIsSuccess(true);
      }

      setIsCalculating(false);
    }, 500);
  };

  const handleCloseModal = () => {
    setIsSuccess(false);
    setErrorMessage('');
  };

  return (
    <div className="word-ladder-container">
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter start word"
          value={startWord}
          onChange={handleStartWordChange}
        />
        <input
          type="text"
          className="form-control"
          placeholder="Enter end word"
          value={endWord}
          onChange={handleEndWordChange}
        />
        <button
          onClick={handleFindWordLadder}
          className="btn btn-primary animate__animated animate__heartBeat"
          disabled={isButtonDisabled || isCalculating}
        >
          {isCalculating ? (
            <SyncLoader
              //css={css`
                //margin-right: 5px;
              //`}
              size={8}
              color={'#fff'}
            />
          ) : (
            'Find Word Ladder'
          )}
        </button>
      </div>

      {/* Solution */}
      {showResult && (
        <div>
          <h3 className="text-center">Word Ladder Result:</h3>
          <div className="scrollable-list">
            <ul className="list-unstyled text-center">
              {wordLadder.map((word, index) => (
                <li key={index} className="animate__animated animate__fadeInUp">
                  {word}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}

      {/* Modal Dialog */}
      <Modal
        isOpen={isSuccess || errorMessage !== ''}
        onRequestClose={handleCloseModal}
        className="custom-modal"
        overlayClassName="custom-modal-overlay"
      >
        {isSuccess && (
          <div>
            <h3 className="text-center">Word Ladder Result:</h3>
            <div className="scrollable-list">
              <ul className="list-unstyled text-center">
                {wordLadder.map((word, index) => (
                  <li key={index} className="animate__animated animate__fadeInUp">
                    {word}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}

        {errorMessage !== '' && (
          <div>
            <p className="error-message text-center">{errorMessage}</p>
            <button onClick={handleCloseModal} className="btn btn-primary">
              Close
            </button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default WordLadder;
