import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { SyncLoader } from 'react-spinners';

import './App.css';

Modal.setAppElement('#root');

const WordLadder = () => {
  const [startWord, setStartWord] = useState('');
  const [endWord, setEndWord] = useState('');
  const [wordLadder, setWordLadder] = useState([]);
  const [showResult, setShowResult] = useState(false);
  const [isCalculating, setIsCalculating] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(true);
  const [isSuccess, setIsSuccess] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const wordList = new Set([
    'cat', 'cot', 'dog', 'dot', 'log', 'lot', 'hot', // Add more words here
  ]);

  useEffect(() => {
    setIsButtonDisabled(startWord === '' || endWord === '' || startWord.length !== endWord.length);
  }, [startWord, endWord]);

  const handleStartWordChange = (e) => {
    setStartWord(e.target.value.trim().toLowerCase().replace(/[^a-z]/g, ''));
  };

  const handleEndWordChange = (e) => {
    setEndWord(e.target.value.trim().toLowerCase().replace(/[^a-z]/g, ''));
  };

  const findWordLadder = () => {
    setIsCalculating(true);

    const queue = [[startWord, [startWord]]];
    const visited = new Set();

    while (queue.length > 0) {
      const [currentWord, currentLadder] = queue.shift();

      if (currentWord === endWord) {
        setIsSuccess(true);
        setWordLadder(currentLadder);
        setIsCalculating(false);
        setShowResult(true);
        return;
      }

      for (let i = 0; i < currentWord.length; i++) {
        for (let j = 97; j <= 122; j++) {
          const newChar = String.fromCharCode(j);
          const nextWord = currentWord.slice(0, i) + newChar + currentWord.slice(i + 1);

          if (!visited.has(nextWord) && wordList.has(nextWord)) {
            visited.add(nextWord);
            queue.push([nextWord, [...currentLadder, nextWord]]);
          }
        }
      }
    }

    setIsCalculating(false);
    setShowResult(true);
    setIsSuccess(false);
    setErrorMessage('Word ladder not found.');
  };

  const handleFindWordLadder = () => {
    if (!isButtonDisabled && !isCalculating) {
      setShowResult(false);
      setIsSuccess(false);
      setErrorMessage('');
      setWordLadder([]);
      setIsCalculating(true);

      setTimeout(findWordLadder, 500);
    }
  };

  const handleCloseModal = () => {
    setIsSuccess(false);
    setErrorMessage('');
    setShowResult(false);
    setStartWord('');
    setEndWord('');
    setWordLadder([]);
  };

  const ladderSteps = [];
  for (let i = 0; i < wordLadder.length - 1; i++) {
    const fromWord = wordLadder[i];
    const toWord = wordLadder[i + 1];
    if (fromWord !== toWord) {
      ladderSteps.push(
        <div key={i} className="ladder-step">
          <div className="ladder-line" />
          <div className="ladder-word">{toWord}</div>
        </div>
      );
    }
  }

  return (
    <div className="container d-flex align-items-center justify-content-center vh-100">
      <div className="word-ladder-container">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Enter start word"
            value={startWord}
            onChange={handleStartWordChange}
          />
        {showResult && isSuccess && !isCalculating && (
          <div className="word-ladder-visualization">
            <div className="ladder-steps-container">
              <div className="ladder-steps">
                <div className="ladder-step">
                  <div className="ladder-word">{startWord}</div>
                </div>
                {ladderSteps}
                <div className="ladder-step">
                  <div className="ladder-word">{endWord}</div>
                </div>
              </div>
            </div>
          </div>
        )}
          <input
            type="text"
            className="form-control"
            placeholder="Enter end word"
            value={endWord}
            onChange={handleEndWordChange}
          />
          <button
            onClick={handleFindWordLadder}
            className="btn btn-primary animate__animated animate__heartBeat"
            disabled={isButtonDisabled || isCalculating}
          >
            {isCalculating ? (
              <SyncLoader
                size={8}
                color={'#fff'}
              />
            ) : (
              'Find Word Ladder'
            )}
          </button>
        </div>


        {/* Modal Dialog */}
        <Modal
          isOpen={isSuccess || errorMessage !== ''}
          onRequestClose={handleCloseModal}
          className="custom-modal"
          overlayClassName="custom-modal-overlay"
        >
          {/* ... (existing content for modal dialog remains the same) */}
        </Modal>
      </div>
    </div>
  );
};

export default WordLadder;
