import React, { useState } from 'react';

const SmallAlert = ({ message, type }) => {
  const [visible, setVisible] = useState(true);

  const handleClose = () => {
    setVisible(false);
  };

  return visible ? (
    <div className={`small-alert ${type}`}>
      <span>{message}</span>
      <button onClick={handleClose}>&times;</button>
    </div>
  ) : null;
};

export default SmallAlert;
