import React, { useState } from 'react';
import { findLadder } from 'word-ladder-js';
import './WordLadder.css'; // Import the custom WordLadder.css file
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const WordLadder = () => {
  const [startWord, setStartWord] = useState('');
  const [endWord, setEndWord] = useState('');
  const [wordLadder, setWordLadder] = useState([]);
  const [showResult, setShowResult] = useState(false); // State to control animation

  const findWordLadder = () => {
    const wordList = [
      "hot", "dot", "dog", "lot", "log", "cog",
      "pot", "pig", "sog", "sot", "cot", "bog",
      "bot", "big", "bit"
    ];
    const ladder = findLadder(startWord, endWord, wordList);
    setWordLadder(ladder);
    setShowResult(true); // Show the result section with animation
  };

  return (
    <div className="word-ladder-container">
      <h1 className="text-center">Word Ladder</h1>
      <div className="row justify-content-center">
        <div className="col-md-4">
          <div className="input-container">
            <input
              type="text"
              value={startWord}
              onChange={(e) => setStartWord(e.target.value)}
              className="form-control mb-2"
              placeholder="Enter start word"
            />
            <input
              type="text"
              value={endWord}
              onChange={(e) => setEndWord(e.target.value)}
              className="form-control mb-2"
              placeholder="Enter end word"
            />
            <button
              onClick={findWordLadder}
              className="btn btn-primary animate__animated animate__rubberBand"
            >
              Find Word Ladder
            </button>
          </div>
        </div>
        <div className={`col-md-4 ${showResult ? 'visible' : 'invisible'}`}>
          {wordLadder.length > 0 && (
            <div className="result-container mt-4">
              <h3 className="text-center">Word Ladder Result:</h3>
              <div className="scrollable-list">
                <ul className="list-unstyled text-center">
                  {wordLadder.map((word, index) => (
                    <li key={index} className="animate__animated animate__fadeInUp">
                      {word}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WordLadder;
